     <td><?=$list['c_name']; ?></td>
     <td><?=$list['cat_id']; ?></td>
          <td><?=$list['date']; ?></td>
      <td>

<a href="delete-user.php?delcom=<?php echo $list['id'];?>"><span class="spa"><i class="fa fa-trash"></i></span></a>
